mapper.select:
SELECT m.*,p.name project,t.name  type, p1.name product FROM manuscript m
LEFT JOIN project p
ON m.project_id=p.id
LEFT JOIN manuscript_type t
ON m.type_id = t.id
LEFT JOIN project_product p1
ON m.product_id = p1.id


           and p.id =#{projectId}


           and m.title like '%${title}%'


           and p1.id=#{product}


           and t.id=#{type}


           and m.check_state=#{checkState}


    order by id desc
    LIMIT  #{startIndex},#{pageSize}

SELECT count(1) FROM manuscript m
LEFT JOIN project p
ON m.project_id=p.id
LEFT JOIN manuscript_type t
ON m.type_id = t.id
LEFT JOIN project_product p1
ON m.product_id = p1.id


        and p.id =#{projectId}


        and m.title like '%${title}%'


        and p1.id=#{product}


        and t.id=#{type}


        and m.check_state=#{checkState}

SELECT m.*,p.name project,t.name type, p1.name product FROM manuscript m LEFT JOIN project p ON m.project_id=p.id LEFT JOIN manuscript_type t ON m.type_id = t.id LEFT JOIN project_product p1 ON m.product_id = p1.id
and m.check_state!=0
and p.name like '%${project}%'
and m.title like '%${title}%'
and p1.id=#{product}
and t.id=#{type}
and m.check_state=#{checkState}
order by id desc LIMIT #{startIndex},#{pageSize}

SELECT count(1) FROM manuscript m LEFT JOIN project p ON m.project_id=p.id LEFT JOIN manuscript_type t ON m.type_id = t.id LEFT JOIN project_product p1 ON m.product_id = p1.id
and m.check_state!=0
and p.name like '%${project}%'
and m.title like '%${title}%'
and p1.id=#{product}
and t.id=#{type}
and m.check_state=#{checkState}
