mapper.select:
SELECT tst.*,pp.name AS product_name from t_select_topic tst
LEFT JOIN project_product pp ON tst.product_id = pp.id
LEFT JOIN project p ON pp.project_id =p.id
LEFT JOIN  manuscript_type mt ON tst.type_id = mt.id


        and tst.is_selected = #{selectType}


        and tst.title like concat('%',#{title},'%')



        and p.name like concat('%',#{projectName},'%')


        and pp.name like concat('%',#{productName},'%')


        and tst.char_count >= #{charCountLimit}


        and tst.char_count <= #{charCountMax}


order by id desc
LIMIT  #{startIndex},#{pageSize}
