mapper.select:
SELECT twst.*,pp.name AS product_name from t_write_selected_topic twst
LEFT JOIN project_product pp ON twst.product_id = pp.id
LEFT JOIN project p ON pp.project_id =p.id
LEFT JOIN  manuscript_type mt ON twst.type_id = mt.id


        and twst.is_complete = #{isComplete}


        and tst.title like concat('%',#{title},'%')


        and twst.type_id = #{typeId}


        and p.name like concat('%',#{projectName},'%')


        and pp.name like concat('%',#{productName},'%')


        and tst.char_count >= #{charCountLimit}


        and tst.char_count <= #{charCountMax}


order by id desc
LIMIT  #{startIndex},#{pageSize}
